package com.authority.device.controller;import com.authority.device.payload.request.DeviceRequest;import com.authority.device.payload.response.DeviceResponse;import com.authority.device.service.DeviceService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/device")public class DeviceController { @Autowired private DeviceService deviceService;    @GetMapping    public List<DeviceResponse> getAllActiveDevices() {        return deviceService.getAllActiveDevices();    }    @PostMapping    public DeviceResponse addDevice(@RequestBody DeviceRequest deviceRequest) {        return deviceService.addDevice(deviceRequest);    }    @PutMapping("/{deviceId}")    public DeviceResponse updateDevice(@PathVariable String deviceId, @RequestBody DeviceRequest deviceRequest) {        return deviceService.updateDevice(deviceId, deviceRequest);    }    @DeleteMapping("/{deviceId}")    public void deleteDevice(@PathVariable String deviceId) {        deviceService.deleteDevice(deviceId);    }}