package com.authority.device.exception;import lombok.Getter;@Getterpublic class DeviceAuthorityManagerException extends RuntimeException {        private final ErrorType errorType;        public DeviceAuthorityManagerException(ErrorType errorType) {            super(errorType.getMessage());            this.errorType = errorType;        }        public DeviceAuthorityManagerException(ErrorType errorType, String customMessage) {            super(customMessage);            this.errorType = errorType;        }    }