package com.authority.device.model;import com.sun.istack.NotNull;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import javax.persistence.*;import java.time.ZonedDateTime;import java.util.UUID;@Builder@NoArgsConstructor@AllArgsConstructor@Table(name = "devices")@Entity@Datapublic class Device {    @Id    @Column(name = "DEVICEID")    private String  deviceId;    @NotNull    @Column(name = "ACCOUNTNO")    private  String accountNo;    @NotNull    @Column(name = "OSNAME")    private  String osName;    @NotNull    @Column(name = "OSVERSION")    private  String osVersion;    @NotNull    @Column(name = "PLATFORM")    private  String platform;    @Column(name = "CREATIONTIME")    private ZonedDateTime creationTime;    @Column(name = "UPDATETIME")    private ZonedDateTime updateTime;    @NotNull    @Column(name = "STATE")    private int state;}