package com.authority.device.repository;import com.authority.device.model.Device;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.List;import java.util.Optional;@Repositorypublic interface DeviceRepository extends JpaRepository<Device, Long> {    List<Device> findByState(int state);    Optional<Device> findByDeviceId(String deviceId);}